contracts/OmniPayCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IOmniPayNotifier {
    function notifyPaymentSuccess(
        address payer,
        address payee,
        address token,
        uint256 amount,
        string calldata paymentRef
    ) external;

    function notifyPaymentFailure(
        address payer,
        address payee,
        address token,
        uint256 amount,
        string calldata paymentRef,
        string calldata reason
    ) external;
}

contract OmniPayCore is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Transaction {
        address payer;
        address payee;
        address token; // address(0) for ETH
        uint256 amount;
        uint256 timestamp;
        string paymentRef;
    }

    event PaymentInitiated(
        address indexed payer,
        address indexed payee,
        address indexed token,
        uint256 amount,
        string paymentRef
    );

    event PaymentCompleted(
        uint256 indexed txId,
        address indexed payer,
        address indexed payee,
        address token,
        uint256 amount,
        string paymentRef
    );

    Transaction[] public transactions;
    address public notifier;

    constructor(address _notifier) {
        notifier = _notifier;
    }

    function setNotifier(address _notifier) external onlyOwner {
        notifier = _notifier;
    }

    /// @notice Pay in ETH to a payee with a payment reference
    /// @param payee Recipient of the payment
    /// @param paymentRef Arbitrary reference string for the payment
    function payETH(address payable payee, string calldata paymentRef)
        external
        payable
        nonReentrant
    {
        require(payee != address(0), "Invalid payee");
        require(msg.value > 0, "No ETH sent");

        emit PaymentInitiated(msg.sender, payee, address(0), msg.value, paymentRef);

        (bool ok, ) = payee.call{value: msg.value}("");
        require(ok, "ETH transfer failed");

        transactions.push(
            Transaction({
                payer: msg.sender,
                payee: payee,
                token: address(0),
                amount: msg.value,
                timestamp: block.timestamp,
                paymentRef: paymentRef
            })
        );
        uint256 txId = transactions.length - 1;
        emit PaymentCompleted(txId, msg.sender, payee, address(0), msg.value, paymentRef);

        if (notifier != address(0)) {
            try IOmniPayNotifier(notifier).notifyPaymentSuccess(
                msg.sender,
                payee,
                address(0),
                msg.value,
                paymentRef
            ) {} catch {
                // Best-effort notification; ignore failures
            }
        }
    }

    /// @notice Pay in ERC20 to a payee with a payment reference
    /// @param token ERC-20 token contract address
    /// @param payee Recipient of the payment
    /// @param amount Token amount to transfer
    /// @param paymentRef Arbitrary reference string for the payment
    function payERC20(
        IERC20 token,
        address payee,
        uint256 amount,
        string calldata paymentRef
    ) external nonReentrant {
        require(address(token) != address(0), "Invalid token");
        require(payee != address(0), "Invalid payee");
        require(amount > 0, "Amount = 0");

        emit PaymentInitiated(msg.sender, payee, address(token), amount, paymentRef);

        token.safeTransferFrom(msg.sender, payee, amount);

        transactions.push(
            Transaction({
                payer: msg.sender,
                payee: payee,
                token: address(token),
                amount: amount,
                timestamp: block.timestamp,
                paymentRef: paymentRef
            })
        );
        uint256 txId = transactions.length - 1;
        emit PaymentCompleted(txId, msg.sender, payee, address(token), amount, paymentRef);

        if (notifier != address(0)) {
            try IOmniPayNotifier(notifier).notifyPaymentSuccess(
                msg.sender,
                payee,
                address(token),
                amount,
                paymentRef
            ) {} catch {
                // ignore notifier failures
            }
        }
    }

    /// @notice Returns the total number of recorded transactions
    function transactionCount() external view returns (uint256) {
        return transactions.length;
    }
}

contracts/OmniPayNotifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

/// @notice Minimal PUSH Comm interface used for notifications.
/// The exact signature may vary; adjust if needed during integration.
interface IPUSHCommInterface {
    function sendNotification(address channel, address recipient, bytes calldata identity) external;
}

contract OmniPayNotifier is Ownable {
    IPUSHCommInterface public pushComm;
    address public channel;

    event NotifierConfigured(address indexed pushComm, address indexed channel);

    event BeforeRenewal(
        uint256 indexed subId,
        address indexed subscriber,
        address indexed merchant,
        uint256 amount,
        uint256 nextPaymentDue
    );
    event PaymentSuccess(
        address indexed payer,
        address indexed payee,
        address indexed token,
        uint256 amount,
        string paymentRef
    );
    event PaymentFailure(
        address indexed payer,
        address indexed payee,
        address indexed token,
        uint256 amount,
        string paymentRef,
        string reason
    );
    event SubscriptionExecuted(
        uint256 indexed subId,
        address indexed subscriber,
        address indexed merchant,
        address token,
        uint256 amount,
        uint256 nextPaymentDue
    );
    event SubscriptionCancelled(uint256 indexed subId, address indexed subscriber, address indexed merchant);
    event PushAttempt(address indexed recipient, bool success, bytes identity);

    constructor(address _pushComm, address _channel) {
        if (_pushComm != address(0)) {
            pushComm = IPUSHCommInterface(_pushComm);
        }
        channel = _channel;
        emit NotifierConfigured(_pushComm, _channel);
    }

    function setPushComm(address _pushComm) external onlyOwner {
        pushComm = IPUSHCommInterface(_pushComm);
        emit NotifierConfigured(address(pushComm), channel);
    }

    function setChannel(address _channel) external onlyOwner {
        channel = _channel;
        emit NotifierConfigured(address(pushComm), channel);
    }

    /// @notice Notify subscriber before renewal is due
    function notifyBeforeRenewal(
        uint256 subId,
        address subscriber,
        address merchant,
        uint256 amount,
        uint256 nextPaymentDue
    ) external {
        emit BeforeRenewal(subId, subscriber, merchant, amount, nextPaymentDue);
        _push(subscriber, _identity("Renewal upcoming", "Your subscription is due soon."));
    }

    /// @notice Notify parties after payment success
    function notifyPaymentSuccess(
        address payer,
        address payee,
        address token,
        uint256 amount,
        string calldata paymentRef
    ) external {
        emit PaymentSuccess(payer, payee, token, amount, paymentRef);
        _push(payer, _identity("Payment Success", "Your payment completed successfully."));
        _push(payee, _identity("Payment Received", "You received a payment."));
    }

    /// @notice Notify parties after payment failure
    function notifyPaymentFailure(
        address payer,
        address payee,
        address token,
        uint256 amount,
        string calldata paymentRef,
        string calldata reason
    ) external {
        emit PaymentFailure(payer, payee, token, amount, paymentRef, reason);
        _push(payer, _identity("Payment Failed", reason));
        _push(payee, _identity("Payment Failed", reason));
    }

    /// @notice Notify after subscription executed
    function notifySubscriptionExecuted(
        uint256 subId,
        address subscriber,
        address merchant,
        address token,
        uint256 amount,
        uint256 nextPaymentDue
    ) external {
        emit SubscriptionExecuted(subId, subscriber, merchant, token, amount, nextPaymentDue);
        _push(subscriber, _identity("Subscription Paid", "Your subscription was charged."));
        _push(merchant, _identity("Payment Received", "A subscription payment was received."));
    }

    /// @notice Notify after subscription cancelled
    function notifySubscriptionCancelled(uint256 subId, address subscriber, address merchant) external {
        emit SubscriptionCancelled(subId, subscriber, merchant);
        _push(subscriber, _identity("Subscription Cancelled", "You cancelled your subscription."));
        _push(merchant, _identity("Subscription Cancelled", "A subscriber cancelled their subscription."));
    }

    function _push(address recipient, bytes memory identity) internal {
        if (address(pushComm) == address(0) || channel == address(0)) {
            emit PushAttempt(recipient, false, identity);
            return;
        }
        try pushComm.sendNotification(channel, recipient, identity) {
            emit PushAttempt(recipient, true, identity);
        } catch {
            emit PushAttempt(recipient, false, identity);
        }
    }

    function _identity(string memory title, string memory body) internal pure returns (bytes memory) {
        // Simple identity payload (title||body)  adjust to PUSH spec as needed
        string memory payload = string(abi.encodePacked("title::", title, "||body::", body));
        return bytes(payload);
    }
}

contracts/OmniPaySubscription.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IOmniPayNotifierSub {
    function notifySubscriptionExecuted(
        uint256 subId,
        address subscriber,
        address merchant,
        address token,
        uint256 amount,
        uint256 nextPaymentDue
    ) external;

    function notifySubscriptionCancelled(uint256 subId, address subscriber, address merchant) external;
}

contract OmniPaySubscription is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Subscription {
        uint256 id;
        address subscriber;
        address merchant;
        address token; // address(0) for ETH
        uint256 amount;
        uint256 interval; // seconds
        uint256 nextPaymentDue;
        bool active;
    }

    event SubscriptionCreated(
        uint256 indexed id,
        address indexed subscriber,
        address indexed merchant,
        address token,
        uint256 amount,
        uint256 interval,
        uint256 nextPaymentDue
    );
    event SubscriptionExecuted(
        uint256 indexed id,
        address indexed subscriber,
        address indexed merchant,
        address token,
        uint256 amount,
        uint256 nextPaymentDue
    );
    event SubscriptionCancelled(uint256 indexed id, address indexed subscriber, address indexed merchant);

    uint256 public nextId = 1;
    mapping(uint256 => Subscription) public subscriptions;
    address public notifier;

    constructor(address _notifier) {
        notifier = _notifier;
    }

    function setNotifier(address _notifier) external onlyOwner {
        notifier = _notifier;
    }

    /// @notice Create a new subscription
    /// @param merchant Merchant to receive payments
    /// @param token ERC-20 token address or address(0) for ETH
    /// @param amount Amount per interval (ETH wei or token units)
    /// @param intervalSeconds Interval in seconds between payments
    function createSubscription(
        address merchant,
        address token,
        uint256 amount,
        uint256 intervalSeconds
    ) external returns (uint256 id) {
        require(merchant != address(0), "Invalid merchant");
        require(amount > 0, "Amount = 0");
        require(intervalSeconds >= 60, "Interval too short");

        id = nextId++;
        subscriptions[id] = Subscription({
            id: id,
            subscriber: msg.sender,
            merchant: merchant,
            token: token,
            amount: amount,
            interval: intervalSeconds,
            nextPaymentDue: block.timestamp + intervalSeconds,
            active: true
        });

        emit SubscriptionCreated(id, msg.sender, merchant, token, amount, intervalSeconds, subscriptions[id].nextPaymentDue);
    }

    /// @notice Execute a due subscription payment
    /// @param id Subscription id
    function executeSubscription(uint256 id) external payable nonReentrant {
        Subscription storage s = subscriptions[id];
        require(s.active, "Inactive");
        require(s.subscriber == msg.sender, "Only subscriber");
        require(block.timestamp >= s.nextPaymentDue, "Not due");

        if (s.token == address(0)) {
            require(msg.value == s.amount, "Incorrect ETH amount");
            (bool ok, ) = payable(s.merchant).call{value: s.amount}("");
            require(ok, "ETH transfer failed");
        } else {
            IERC20(s.token).safeTransferFrom(s.subscriber, s.merchant, s.amount);
        }

        s.nextPaymentDue = block.timestamp + s.interval;
        emit SubscriptionExecuted(id, s.subscriber, s.merchant, s.token, s.amount, s.nextPaymentDue);

        if (notifier != address(0)) {
            try IOmniPayNotifierSub(notifier).notifySubscriptionExecuted(
                id,
                s.subscriber,
                s.merchant,
                s.token,
                s.amount,
                s.nextPaymentDue
            ) {} catch {
                // ignore notifier failures
            }
        }
    }

    /// @notice Cancel an active subscription
    /// @param id Subscription id
    function cancelSubscription(uint256 id) external {
        Subscription storage s = subscriptions[id];
        require(s.active, "Already inactive");
        require(s.subscriber == msg.sender, "Only subscriber");
        s.active = false;
        emit SubscriptionCancelled(id, s.subscriber, s.merchant);

        if (notifier != address(0)) {
            try IOmniPayNotifierSub(notifier).notifySubscriptionCancelled(id, s.subscriber, s.merchant) {} catch {}
        }
    }
}

